/**
 * Type-safe i18n translation keys
 *
 * This file will be auto-generated by ampel-i18n-builder in Phase 2.
 * For now, we use string literals for translation keys.
 */

/**
 * Translation key for the common namespace
 */
export type CommonKey = string;

/**
 * Translation key for the dashboard namespace
 */
export type DashboardKey = string;

/**
 * Translation key for the settings namespace
 */
export type SettingsKey = string;

/**
 * Translation key for the errors namespace
 */
export type ErrorsKey = string;

/**
 * Translation key for the validation namespace
 */
export type ValidationKey = string;

/**
 * All translation keys across all namespaces
 */
export type TranslationKey = CommonKey | DashboardKey | SettingsKey | ErrorsKey | ValidationKey;
